import ReactQuill from "react-quill";
import "react-quill/dist/quill.snow.css";

const style = {
  minHeight: "200px", // Set the initial height as needed
};

const modules = {
  toolbar: [
    [{ header: [1, 2, false] }],
    ["bold", "italic", "underline", "strike", "blockquote"],
    [
      { list: "ordered" },
      { list: "bullet" },
      { indent: "-1" },
      { indent: "+1" },
    ],
    ["link", "image"],
    ["clean"],
  ],
};

const formats = [
  "header",
  "bold",
  "italic",
  "underline",
  "strike",
  "blockquote",
  "list",
  "bullet",
  "indent",
  "link",
  "image",
];

function Editor({ value, onChange }) {
  return (
    <ReactQuill
      style={style}
      modules={modules}
      formats={formats}
      value={value}
      onChange={onChange}
    />
  );
}

export default Editor;
